package hardcastle.com.churchapplication.Utils;import hardcastle.com.churchapplication.model.EventsResponseBean;import hardcastle.com.churchapplication.model.LoginResponceBean;import hardcastle.com.churchapplication.model.ResponseBean;import hardcastle.com.churchapplication.model.SermonsResponseBean;import hardcastle.com.churchapplication.model.UpdateUserBean;import retrofit2.Call;import retrofit2.http.Body;import retrofit2.http.Field;import retrofit2.http.FormUrlEncoded;import retrofit2.http.Headers;import retrofit2.http.POST;public interface GetDataService {    @Headers("Media-Type: application/json; charset=utf-8")    @POST("login_user.php")    Call<LoginResponceBean> getLoginResponse(@Body String body);    @Headers("Content-Type: application/json")    @POST("register.php")    Call<ResponseBean> userRegistration(@Body String body);    @Headers("Content-Type: application/json")    @POST("get_sermons_videos.php")    Call<SermonsResponseBean> getSermonsList();    @Headers("Content-Type: application/json")    @POST("get_event_details.php")    Call<EventsResponseBean> getEventList();    @Headers("Content-Type: application/json")    @POST("update_info.php")    Call<UpdateUserBean> updateUser(@Body String body);    @FormUrlEncoded    @POST("login_user.php")    Call<LoginResponceBean> login(            @Field("TYPE") String TYPE,            @Field("username") String username,            @Field("password") String password    );}